sql.where:
b.board_type = #{boardType} AND
DATE_FORMAT(b.create_date, '%Y-%m-%d') BETWEEN #{fromDate} AND #{toDate}
DATE_FORMAT(b.create_date, '%Y-%m-%d') BETWEEN DATE_FORMAT(DATE_SUB(CURDATE(), INTERVAL 1 MONTH),'%Y-%m-%d') AND DATE_FORMAT(NOW(),'%Y-%m-%d')
AND b.category_id = #{searchCategory}
AND (b.board_title LIKE CONCAT('%', #{search}, '%') OR b.board_content LIKE CONCAT('%', #{search}, '%'))
