mapper.select:
SELECT c.category_name, /* 카테고리 이름 */ g.gallery_thumb_name, /* 갤러리 썸네일 이름*/ (SELECT COUNT(*) FROM gallery_detail g WHERE b.board_id = g.board_id) AS gallery_cnt, /* 갤러리 개수 */ IF (b.create_date >= DATE(NOW()) - INTERVAL #{newDay} DAY, TRUE, FALSE) AS new_status, /* 신규 상태 */
FROM board b JOIN category c ON c.category_id = b.category_id JOIN (SELECT board_id, MIN(gallery_order) AS min_order /* 순서가 빠른 썸네일 번호 */ FROM gallery_detail GROUP BY board_id) gd ON b.board_id = gd.board_id JOIN gallery_detail g ON b.board_id = g.board_id AND g.gallery_order = min_order
LIMIT #{limit} OFFSET #{offset}
